mapper.select:
SELECT *
  FROM USER
 WHERE id = #{id}
   AND password = #{password}

mapper.insert:
INSERT INTO USER
(
  id
, password
, user_name
, user_email
, user_phone_number
, user_organization
, user_gender
, user_date_birth
)
VALUES
(
    #{id}
  , #{password}
  , #{userName}
  , #{userEmail}
  , #{userPhoneNumber}
  , #{userOrganization}
  , #{userGender}
  , #{userDateBirth}
)

mapper.update:
UPDATE USER
SET password = #{newPassword}
WHERE id = #{id}

UPDATE USER
SET user_name = #{userName}, email = #{email}, phone_number = #{phoneNumber}, organization = #{organization}, gender = #{gender}, date_birth = #{dateBirth}
WHERE id = #{id}
